@import "_variables.scss";

body {
  color: $main-color;
  font-family: "Fjalla One", sans-serif;
}

// main section
.main-section {
  overflow-x: hidden;
  padding: 0 10px;
  width: 100%;
  height: 100vh;
  display: flex;
  align-items: center;
  position: relative;
  z-index: 998;
  &__text-content {
    z-index: 999;
    color: $main-color;
    margin-left: 80px;
    width: 490px;
    max-width: 100%;
    h1 {
      font-size: 86px;
      margin-bottom: 90px;
    }
    p {
      font-size: 36px;
      margin-bottom: 75px;
      line-height: 45px;
    }
    a {
      font-size: 26px;
    }
  }
  &__image-content {
    position: absolute;
    height: 100vh;
    top: 0;
    right: 0;
    overflow: hidden;
    .head-svg {
      position: relative;
      left: calc(100% - 80%);
      svg {
        width: 100%;
        max-width: 100%;
        height: 100vh;
      }
    }
    .parallax-image {
      img {
        position: absolute;
        top: 0;
        right: 0;
        width: 60px;
        height: 60px;
      }
      .address-book {
        top: 5%;
        right: 20%;
      }
      .book {
        top: 13%;
        right: 25%;
      }
      .study {
        top: 9%;
        right: 7%;
      }
      .homework {
        top: 22%;
        right: 10%;
      }
      .study2 {
        top: 15%;
        right: 15%;
      }
    }
  }
}

// add section
.add-section {
  padding: 60px 20px;
  width: 100%;
  height: 1000px;
  display: flex;
  flex-direction: column;
  align-items: center;
  &__form {
    display: flex;
    align-items: center;
    justify-content: center;
    flex-direction: column;
    width: 550px;
    max-width: 100%;
    .input-wrapper,
    .rating-category-inputs,
    button {
      width: 100%;
    }
    .input-wrapper {
      margin-bottom: 20px;
    }
    .input-wrapper {
      min-height: 75px;
    }
    input,
    select {
      background: transparent;
      border: 2px solid $main-color;
      border-radius: 11px;
      padding: 23px 20px 19px;
      color: $main-color;
      font-size: 24px;
    }
    input {
      height: 75px;
      width: 100%;
    }
    input::placeholder,
    select::placeholder {
      font-size: 24px;
      line-height: 30px;
      text-transform: uppercase;
      color: $main-color;
    }
    .error-field {
      // display: none;
      opacity: 0;
      transform: scale(0);
      width: 100%;
      min-height: 30px;
      margin-top: 10px;
      padding: 10px 5px;
      border-radius: 5px;
      background-color: rgb(232, 32, 32);
      transition: opacity 0.4s, transform 0.4s;
      &.show {
        display: block;
        opacity: 1;
        transform: scale(1);
      }
      p {
        font-size: 18px;
      }
    }

    .rating-category-inputs {
      display: flex;
      .input-wrapper,
      select {
        width: 265px;
      }
      .input-wrapper {
        &:first-child {
          margin-right: 10px;
        }
        &:last-child {
          margin-left: 10px;
        }
      }
      select {
        padding-left: 10px;
        max-height: 75px;
        option {
          font-family: "Fjalla One", sans-serif;
          background-color: $main-color;
          color: #0d152b;
        }
      }
    }
    button {
      height: 75px;
      border-radius: 11px;
      border: 2px solid $main-color;
      background-color: transparent;
      transition: background-color 0.5s;
      span {
        font-size: 24px;
        text-transform: uppercase;
        color: #0d152b;
      }
      &:hover {
        background-color: $main-color;
      }
    }
  }
}

// booklist section
.book-list-section {
  padding: 0 20px;
  padding-top: 60px;
  width: 100%;
  height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  &__title {
    text-transform: uppercase;
  }
}

@media (max-width: 1600px) {
  .main-section__text-content {
    width: 445px;
    h1 {
      font-size: 70px;
      margin-bottom: 70px;
    }
    p {
      font-size: 32px;
      line-height: 41px;
      margin-bottom: 65px;
    }
  }
}

@media (max-width: 1200px) {
  .main-section__text-content {
    width: 350px;
    h1 {
      font-size: 55px;
      margin-bottom: 55px;
    }
    p {
      font-size: 26px;
      line-height: 35px;
      margin-bottom: 50px;
    }
    a {
      font-size: 22px;
    }
  }
}

@media (max-width: 1024px) {
  .main-section__image-content {
    width: 100%;
    .head-svg {
      display: none;
    }
    .parallax-image {
      position: relative;
      top: 0;
      left: 0;
      width: 100%;
      height: 100%;
      z-index: 0;
      img {
        width: 65px;
        height: 65px;
      }
      .address-book {
        top: 27%;
        right: 85%;
      }
      .book {
        top: 82%;
        right: 51%;
      }
      .study {
        top: 15%;
        right: 17%;
      }
      .homework {
        top: 41%;
        right: 36%;
      }
      .study2 {
        top: 7%;
        right: 58%;
      }
    }
  }
}

@media (max-width: 767px) {
  .main-section {
    justify-content: center;
    &__text-content {
      text-align: center;
      margin-left: 0;
    }
  }
}

@media (max-width: 550px) {
  .book-list li .item-book {
    padding: 0 18px;
    &__main-info {
      .name {
        font-size: 24px;
        margin-right: 8px;
      }
      .author {
        font-size: 16px;
      }
      .rating {
        width: 90px;
        height: 25px;
        font-size: 18px;
        padding-top: 5px;
        margin-right: 15px;
      }
    }
    &__other-info .edit {
      margin-right: 15px;
    }
  }

  .add-section__form {
    input,
    select {
      height: 65px;
      margin-bottom: 15px;
      padding: 14px 18px 19px;
      font-size: 20px;
    }
    input::placeholder,
    select::placeholder {
      font-size: 20px;
      line-height: 36px;
    }
    .rating-category-inputs {
      display: flex;
      flex-direction: column;
      align-items: center;
      input,
      select {
        width: 100%;
      }
      input {
        margin-right: 0;
      }
      select {
        margin-left: 0;
      }
    }
    button {
      height: 65px;
      span {
        font-size: 20px;
      }
    }
  }
}

@media (max-width: 420px) {
  .section__title {
    font-size: 42px;
    margin-bottom: 75px;
  }

  .main-section__text-content {
    width: 320px;
    h1 {
      font-size: 45px;
      margin-bottom: 40px;
    }
    p {
      font-size: 22px;
      line-height: 30px;
      margin-bottom: 35px;
    }
    a {
      font-size: 20px;
    }
  }
}

@media (max-width: 360px) {
  .book-list li .item-book__main-info {
    .name {
      font-size: 20px;
      margin-right: 5px;
    }
    .author {
      font-size: 14px;
    }
    .rating {
      width: 75px;
      font-size: 14px;
    }
  }
}
